mapper.update:
UPDATE MEMBER 
SET USER_IMG = #{userImg}
WHERE USER_ID = #{userId}

UPDATE MEMBER 
SET USER_INTRODUCE = #{userIntroduce}
WHERE USER_ID = #{userId}

UPDATE MEMBER 
SET USER_NICKNAME = #{userNickname}
WHERE USER_ID = #{userId}

UPDATE MEMBER 
SET USER_PWD = #{newPassword}
WHERE USER_ID = #{userId} 
AND USER_PWD = #{currentPassword}
